#include <Gamebuino-Meta.h>

#include "MenuUI.h"

const bool MenuUI::ITEM_SELECTED          = true;

const uint8_t MenuUI::NO_ITEM_INDEX       = 0;
const uint8_t MenuUI::ALPHA_ITEM_INDEX    = 1;
const uint8_t MenuUI::BETA_ITEM_INDEX     = 2;
const uint8_t MenuUI::GAMMA_ITEM_INDEX    = 3;
const uint8_t MenuUI::DELTA_ITEM_INDEX    = 4;


MenuUI::MenuUI() : 
  currentPageIndex(0),
  maxItems(0)
{}

void MenuUI::initialize(uint8_t * anItems, size_t aNbItems) {
  this->items = anItems;
  this->maxItems = aNbItems;
}

void MenuUI::reset() {
  this->isItemSelectedFlag = ( ! MenuUI::ITEM_SELECTED );
}

void MenuUI::manageCommands() {
  if(gb.buttons.pressed(BUTTON_LEFT)) {
    if(this->currentPageIndex > 0) {
      this->currentPageIndex--;
    }
  } else if(gb.buttons.pressed(BUTTON_RIGHT)) {
    uint8_t nextPageIndex = this->currentPageIndex;
    nextPageIndex++;
    if(nextPageIndex < this->maxItems) {
      this->currentPageIndex = nextPageIndex;
    }
  } else if(gb.buttons.pressed(BUTTON_A)) {
    this->isItemSelectedFlag = MenuUI::ITEM_SELECTED;
  }
}

const uint8_t MenuUI::getCurrentItemIndex() const {
  uint8_t currentItemIndex = MenuUI::NO_ITEM_INDEX;
  if(this->maxItems > 0) {
    currentItemIndex = this->items[ this->currentPageIndex ];
  }
  return currentItemIndex;
}

void MenuUI::rendering() const {
  this->renderingItem();
}

