const bool MenuUI::isItemSelected() const {   return (this->isItemSelectedFlag == MenuUI::ITEM_SELECTED); }

const bool MenuUI::isCurrentAlphaItem() const {     return ( this->getCurrentItemIndex() == MenuUI::ALPHA_ITEM_INDEX ); }
const bool MenuUI::isCurrentBetaItem() const {      return ( this->getCurrentItemIndex() == MenuUI::BETA_ITEM_INDEX ); }
const bool MenuUI::isCurrentGammaItem() const {     return ( this->getCurrentItemIndex() == MenuUI::GAMMA_ITEM_INDEX ); }
const bool MenuUI::isCurrentDeltaItem() const {     return ( this->getCurrentItemIndex() == MenuUI::DELTA_ITEM_INDEX ); }

const bool MenuUI::isAlphaItem() const {    return ( this->isItemSelected() && this->isCurrentAlphaItem() ); }
const bool MenuUI::isBetaItem() const {     return ( this->isItemSelected() && this->isCurrentBetaItem() ); }
const bool MenuUI::isGammaItem() const {    return ( this->isItemSelected() && this->isCurrentGammaItem() ); }
const bool MenuUI::isDeltaItem() const {    return ( this->isItemSelected() && this->isCurrentDeltaItem() ); }
